import React, { Component } from 'react'

// Ramda
import * as R from 'ramda';

// Latex
import 'katex/dist/katex.min.css';
import { InlineMath } from 'react-katex';



let initialState = {
};

class CurrentQuestion extends Component {

    state = initialState;


    handleChange = event => {
        let { name, value } = event.target
        // The [ ] brackets here are the setState synatx
        this.setState({
            [name]: value,
        })
    };

    questionType = (currentNumber, x) => {
        // Multiplication
        if (x == 0) {
            let factorMax = 250; // Max possible mult. factor
            let randFactor = Math.floor(Math.random() * factorMax);
            let answer = currentNumber * randFactor;
            let latex = <InlineMath>
                {randFactor + ' \\times ' + currentNumber}
          </InlineMath>;
            return (
                [answer, latex]
            );
        }
    };

    render() {

        let { guess } = this.state;
        let { handleSubmit, currentNumber, currentQuestionType } = this.props;



        return (

            <div>

                <h4>
                    {this.questionType(currentNumber, currentQuestionType)[1]}
                </h4>

                <form>



                    <label>Guess</label>
                    <input
                        type="number"
                        name="guess"
                        id="guess"
                        value={guess || ''}
                        onChange={this.handleChange}
                        autoFocus />


                </form>

                <input
                    type="button"
                    value="Submit"
                    onClick={() => {
                        if (guess !== 0) {
                            handleSubmit(
                                this.questionType(currentNumber, currentQuestionType)[0],
                                this.state.guess,
                                this.state.guess,
                                this.state.guess
                            );
                            this.setState(initialState);
                        }
                        else {
                            alert('Please enter a number/non-zero overall exponent');
                            this.setState(initialState);
                        }
                    }
                    }
                />

                <h2>Answer</h2>

                <h4>{currentNumber}</h4>

            </div>
        );
    };

}


export default CurrentQuestion
