import React, { Component } from 'react'

// Ramda
import * as R from 'ramda';



let initialState = {
    guess: null,
};

class CurrentQuestion extends Component {

    state = initialState;


    handleChange = event => {
        let { name, value } = event.target
        // The [ ] brackets here are the setState synatx
        this.setState({
          [name]: value,
        })
      }

    render() {

        let { guess } = this.state;
        let { handleSubmit,  currentNumber } = this.props;



        return (

            <div>

                <form>


          <label>Answer</label>
          <input
            type="number"
            name="number"
            id="number"
            value={guess || ''}
            onChange={this.handleChange}
            autoFocus />


        </form>

        <input
          type="button"
          value="Submit"
          onClick={() => {
            if (guess!==0) {
              handleSubmit(
                this.state
              );
              this.setState(initialState);
            }
            else {
              alert('Please enter a number/non-zero overall exponent');
              this.setState(initialState);
            }
          }
          }
        />
        <input
          type="button"
          value="Clear"
          onClick={() => {
            this.setState(initialState);
          }}
        />

            </div>
        );
    };

}


export default CurrentQuestion
