import React, { Component } from 'react'


// Components
import History from './History';
import CurrentQuestion from './CurrentQuestion';

// Importing units and presets
import unitsSet from './data/unitsSet'
import presetsSet from './data/presetsSet'




let initialState = {
  input: [],
  outputUnit: 'm',
  GNis1: false,
  currentNumber: 100,
  currentAnswer: 100,
  previousAnswers: [],
  previousGuesses: [],
  percentCorrect: 1,
  currentQuestion: 0, // Index for question type (multiplicaton, division etc.)
};

class App extends Component {

  state = initialState;

  // Code for handling the submit button and appending new data to input list

  handleSubmit = (x) => {
    this.setState({ input: [...this.state.input, x] });
    this.setState({ currentQuestion: Math.floor(Math.random() * 4) });
  };

  handleReset = () => {
    this.setState(initialState);
  };

  render() {

    let { input, outputUnit, GNis1 } = this.state;

    return (

      <div className="container">
        <h1>
          Mental Math
      </h1>

        <p>
          Simple mental math practice.
      </p>

        <h3>
          Current Question
      </h3>
        <CurrentQuestion
          handleSubmit={this.handleSubmit}
          presetsSet={presetsSet}
          currentNumber={this.state.currentNumber} />

        <h3>
          History
      </h3>
        <History
          input={input}
          GNis1={GNis1}
          handleGNis1Toggle={this.handleGNis1Toggle}
          unitsSet={unitsSet}
          outputUnit={outputUnit}
          handleReset={this.handleReset}
          handleOutputUnitChange={this.handleOutputUnitChange} />
      </div>
    )
  }

}




export default App;
