import React, { Component } from 'react'

// Ramda
import * as R from 'ramda';



let initialState = {
    guess: null,
};

class CurrentQuestion extends Component {

    state = initialState;


    handleChange = event => {
        let { name, value } = event.target
        // The [ ] brackets here are the setState synatx
        this.setState({
            [name]: value,
        })
    }

    render() {

        let { guess } = this.state;
        let { handleSubmit, currentNumber ,currentQuestionType } = this.props;



        return (

            <div>

                <form>


                    <label>Guess</label>
                    <input
                        type="number"
                        name="guess"
                        id="guess"
                        value={guess || ''}
                        onChange={this.handleChange}
                        autoFocus />


                </form>

                <input
                    type="button"
                    value="Submit"
                    onClick={() => {
                        if (guess !== 0) {
                            handleSubmit(
                                this.state.guess,
                                this.state.guess,
                                this.state.guess,
                                this.state.guess
                            );
                            this.setState(initialState);
                        }
                        else {
                            alert('Please enter a number/non-zero overall exponent');
                            this.setState(initialState);
                        }
                    }
                    }
                />

                <h3>Answer</h3>

            </div>
        );
    };

}


export default CurrentQuestion
