// Ramda
import * as R from 'ramda';



let initialState = {
    meterValue: null,
    presetExponent: 1,
    presetName: null,
};

class Presets extends Component {

    state = initialState;



    render() {

        let { number, presetExponent, presetName } = this.state;
        let { handleSubmit, presetsSet, currentNumber } = this.props;


        return (

            <div>
                <input
                    type="button"
                    value="Submit"
                    onClick={() => {
                        if (!number) {
                            alert('Please choose a quantity');
                            this.setState(initialState);
                        } else if (Number(presetExponent)===0) {
                            alert('Please choose a non-zero exponent');
                            this.setState(initialState);
                        }
                        else {
                            handleSubmit(
                                presetExponentiator(this.state, presetExponent)
                            );
                            this.setState(initialState);
                        }
                    }
                    }
                />
            </div>
        );
    };

}


export default Presets
