import React, { Component } from 'react'


// Components
import History from './History';
import CurrentQuestion from './CurrentQuestion';

// Importing units and presets
import unitsSet from './data/unitsSet'
import presetsSet from './data/presetsSet'




let initialState = {
  input: [],
  outputUnit: 'm',
  GNis1: false,
  currentNumber: 10,
  previousAnswers: [],
  previousGuesses: [],
  percentCorrect: 1,
  currentQuestionType: 0, // Index for question type (multiplicaton, division etc.)
  randInt: Math.random(), // Random integer between 0 and 1
};

class App extends Component {

  state = initialState;

  questionTypes = 1; //Number of different types of questions

  // Code for handling the submit button and appending new data to input list

  handleSubmit = (number, answer, guesslist, answerlist) => {
    this.setState({ currentNumber: number });
    this.setState({ currentAnswer: answer });
    this.setState({ previousGuesses: [...this.state.previousGuesses, guesslist] });
    this.setState({ previousAnswers: [...this.state.previousAnswers, answerlist] });
    this.setState({ currentQuestionType: Math.floor(Math.random() * this.questionTypes) });
    this.setState({ randInt: Math.random() });
  };

  handleReset = () => {
    this.setState(initialState);
  };

  render() {

    let { input, outputUnit, GNis1 } = this.state;

    return (

      <div className="container">
        <h1>
          Mental Math
      </h1>

        <p>
          Simple mental math practice.
      </p>

        <h2>
          Current Question {this.state.currentNumber} 
        </h2>
        <CurrentQuestion
          handleSubmit={this.handleSubmit}
          currentNumber={this.state.currentNumber}
          currentQuestionType={this.state.currentQuestionType}
          randInt={this.state.randInt} />

        <h3>
          History
      </h3>
        <History
          input={input}
          GNis1={GNis1}
          handleGNis1Toggle={this.handleGNis1Toggle}
          unitsSet={unitsSet}
          outputUnit={outputUnit}
          handleReset={this.handleReset}
          handleOutputUnitChange={this.handleOutputUnitChange} />
      </div>
    )
  }

}




export default App;
